const e=JSON.parse('{"key":"v-2916a324","path":"/interview/net/network.html","title":"Network","lang":"zh-CN","frontmatter":{"title":"Network","date":"2022-04-02T00:00:00.000Z","timeline":false},"headers":[{"level":2,"title":"1. TCP 三次握手和四次挥手","slug":"_1-tcp-三次握手和四次挥手","link":"#_1-tcp-三次握手和四次挥手","children":[{"level":3,"title":"三次握手","slug":"三次握手","link":"#三次握手","children":[]},{"level":3,"title":"为何需要三次握手","slug":"为何需要三次握手","link":"#为何需要三次握手","children":[]},{"level":3,"title":"四次挥手","slug":"四次挥手","link":"#四次挥手","children":[]},{"level":3,"title":"为何等待 2MSL","slug":"为何等待-2msl","link":"#为何等待-2msl","children":[]},{"level":3,"title":"为何需要四次握手","slug":"为何需要四次握手","link":"#为何需要四次握手","children":[]}]}],"readingTime":{"minutes":2.69,"words":807},"filePathRelative":"interview/net/network.md","localizedDate":"2022年4月2日","excerpt":"<h2> 1. TCP 三次握手和四次挥手</h2>\\n<h3> 三次握手</h3>\\n<figure><img src=\\"https://raw.githubusercontent.com/dreamjz/pics/main/pics/2023/format.png\\" alt=\\"\\" tabindex=\\"0\\" loading=\\"lazy\\"><figcaption></figcaption></figure>\\n<ol>\\n<li>客户端发送<strong>连接请求报文（SYN = 1，seq = x）</strong>，客户端进入 SYN-SENT （<strong>同步已发送</strong>）状态。<mark>TCP 规定，SYN 报文（SYN = 1），不能携带数据，但需要消耗一个序列号。</mark></li>\\n<li>TCP 服务器收到请求报文后，若同意连接，则发出<strong>确认报文（SYN = 1，ACK = 1，ack = x+1，seq = y）</strong>， TCP 服务器进入 **SYN-RCVD （同步接收）**状态。<mark>此报文无法携带数据，需要消耗一个序列号。</mark></li>\\n<li>TCP 客户端收到后，向服务器发送<strong>确认报文（ACK = 1， ack = y+1，seq = x+1）</strong>。此时连接建立，客户端进入<strong>连接已建立 （ESTABLISHED）状态</strong>。<mark>此报文可携带数据，若无数据则不消耗序列号</mark>。</li>\\n<li>当服务器收到客户端的确认报文后，进入**连接已建立（ESTABLISHED）**状态。</li>\\n</ol>"}');export{e as data};
